import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function PackagePage() {
  const { slug } = useParams();
  const [pkg, setPkg] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/package/${slug}`)
      .then((res) => {
        setPkg(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError('Package not found or server error.');
        setLoading(false);
      });
  }, [slug]);

  if (loading) return <p style={{ padding: '2rem' }}>Loading package...</p>;
  if (error) return <p style={{ padding: '2rem', color: 'red' }}>{error}</p>;

  const demoImage = 'https://source.unsplash.com/featured/?travel'; // Random travel image
  const demoDeeplink = 'https://www.google.com/travel/flights'; // Safe working link

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{pkg.title}</h2>
      <img
        src={pkg.imageUrl || demoImage}
        alt={pkg.title}
        style={{ width: '100%', maxHeight: '400px', objectFit: 'cover', marginBottom: '1rem' }}
      />
      <p>{pkg.description}</p>
      <p><strong>Price:</strong> â‚¹{pkg.price.toLocaleString()}</p>

      <a href={pkg.deeplink || demoDeeplink} target="_blank" rel="noopener noreferrer">
        <button style={{ marginTop: '1rem', padding: '10px 20px', cursor: 'pointer' }}>
          View Package on Travel Site
        </button>
      </a>
    </div>
  );
}

export default PackagePage;
