import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function HomePage() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleClick = async () => {
    setLoading(true);
    setError('');

    try {
      const res = await axios.get('http://localhost:5001/api/random-package');
      const { slug } = res.data;
      navigate(`/package/${slug}`);
    } catch (err) {
      console.error('Error fetching package:', err);
      setError('Failed to fetch a package. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h1>Explore Holiday Packages</h1>

      {/* Random Package Button */}
      <button
        onClick={handleClick}
        disabled={loading}
        style={{
          margin: '1rem',
          padding: '1rem 2rem',
          fontSize: '1rem',
          borderRadius: '8px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          cursor: 'pointer'
        }}
      >
        {loading ? 'Loading...' : 'Show Me a Package'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* ðŸ”— Static Image Button for Domestic - using DEMO link */}
      <div style={{ marginTop: '3rem' }}>
        <a
          href="https://example-travel-site.com/domestic-packages"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img
            src="https://images.unsplash.com/photo-1582719478250-dbe4c8b2f668?auto=format&fit=crop&w=1000&q=80"
            alt="Domestic Holiday"
            style={{ width: '60%', borderRadius: '12px', cursor: 'pointer', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' }}
          />
        </a>
        <p>Click the image to explore Domestic Packages</p>
      </div>

      {/* ðŸ”— Text Button for Kerala - using DEMO link */}
      <div style={{ marginTop: '2rem' }}>
        <a
          href="https://example-travel-site.com/kerala-holiday-packages"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            fontSize: '1.2rem',
            color: '#28a745',
            textDecoration: 'none',
            fontWeight: 'bold',
            padding: '0.5rem 1rem',
            border: '2px solid #28a745',
            borderRadius: '8px',
            display: 'inline-block',
            marginTop: '1rem'
          }}
        >
          Explore Kerala Packages â†’
        </a>
      </div>
    </div>
  );
}

export default HomePage;
