import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function PackagePage() {
  const { slug } = useParams();
  const [pkg, setPkg] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/package/${slug}`)
      .then((res) => {
        setPkg(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError('Package not found or server error.');
        setLoading(false);
      });
  }, [slug]);

  if (loading) return <p style={{ padding: '2rem' }}>Loading package...</p>;
  if (error) return <p style={{ padding: '2rem', color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ marginBottom: '1rem' }}>{pkg.title}</h2>

      {/* Demo Image */}
      <img
        src="https://source.unsplash.com/800x400/?travel,holiday"
        alt="Package"
        style={{ width: '100%', borderRadius: '10px', marginBottom: '1rem' }}
      />

      {/* Description */}
      <p style={{ fontSize: '1.1rem', lineHeight: '1.6' }}>{pkg.description}</p>

      {/* Price */}
      <p style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>
        Price: ₹{pkg.price.toLocaleString()}
      </p>

      {/* Book Now Button */}
      <a
        href="https://example.com/book-now"
        target="_blank"
        rel="noopener noreferrer"
        style={{
          display: 'inline-block',
          marginTop: '1.5rem',
          padding: '0.75rem 1.5rem',
          backgroundColor: '#007BFF',
          color: 'white',
          borderRadius: '6px',
          textDecoration: 'none',
          fontWeight: 'bold'
        }}
      >
        Book Now →
      </a>
    </div>
  );
}

export default PackagePage;
